import React, {useEffect, useState} from 'react'
import BackendErrorMessages from 'components/backendErrorMessages';

const ArticleForm = ({onSubmit, errors, initValues}) => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [description, setDescr] = useState('');
    const [tagList, setTagList] = useState('');

    useEffect(() => {
        if(!initValues) {
            return;
        }
        setTitle(initValues.title);
        setBody(initValues.body);
        setDescr(initValues.description);
        setTagList(initValues.tagList.join(' '));
    }, [initValues])

    const handleSubmit = e => {
        e.preventDefault();
        const article = {
            title,
            body,
            description,
            tagList
        }
        onSubmit(article);
    }
    return (
        <div className="aditor-page">
            <div className="container page">
                <div className="row">
                    <div className="col-md-10 offset-md-1 col-xs-12">
                        {errors && <BackendErrorMessages backendErrors={errors}/>}
                        <form onSubmit={handleSubmit}>
                            <fieldset>
                                <fieldset className='form-group'>
                                    <input 
                                    value={title}
                                    onChange={e => setTitle(e.target.value)}
                                    type="text" 
                                    className='form-control form-control-lg' 
                                    placeholder='Article title'/>
                                </fieldset>
                                <fieldset className='form-group'>
                                    <input 
                                    value={body}
                                    onChange={e => setBody(e.target.value)}
                                    type="text" 
                                    className='form-control form-control-lg' 
                                    placeholder='What is this article about?'/>
                                </fieldset>
                                <fieldset className='form-group'>
                                   <textarea 
                                   value={description}
                                   onChange={e => setDescr(e.target.value)}
                                   className='form-control' 
                                   placeholder='Write your article (in markdown)' 
                                   rows="8"></textarea>
                                </fieldset>
                                <fieldset className='form-group'>
                                    <input 
                                    value={tagList}
                                    onChange={e => setTagList(e.target.value)}
                                    type="text" 
                                    className='form-control form-control-lg' 
                                    placeholder='Enter tags'/>
                                </fieldset>
                                <fieldset className='form-group'>
                                    <button type='submit' className="btn btn-lg pull-xs-right btn-primary">
                                        Publish article
                                    </button>
                                </fieldset>
                            </fieldset>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ArticleForm;